import React, { useEffect, useState } from "react";
import axios from "axios";
import MovieCard from "./MovieCard";
import ReactPaginate from "react-paginate";

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const itemsPerPage = 5; // Number of movies per page

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get(
          "https://api.themoviedb.org/3/movie/popular",
          {
            params: { api_key: "c82ab0405fb981cfd52454edfc40eb87" },
          }
        );
        setMovies(response.data.results);
      } catch (error) {
        console.error("Error fetching movies:", error);
      }
    };

    fetchMovies();
  }, []);

  // Pagination logic
  const offset = currentPage * itemsPerPage;
  const currentMovies = movies.slice(offset, offset + itemsPerPage);
  const pageCount = Math.ceil(movies.length / itemsPerPage);

  const handlePageClick = ({ selected }) => {
    setCurrentPage(selected);
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-center">Popular Movies</h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols- gap-6">
        {currentMovies.map((movie) => (
          <MovieCard key={movie.id} movie={movie} />
        ))}
      </div>

      {/* Pagination */}
      <div className="flex justify-center mt-8">
        <ReactPaginate
          previousLabel={"Previous"}
          nextLabel={"Next"}
          breakLabel={"..."}
          pageCount={pageCount}
          marginPagesDisplayed={2}
          pageRangeDisplayed={3}
          onPageChange={handlePageClick}
          containerClassName="flex gap-2"
          pageClassName="px-3 py-1 border rounded hover:bg-blue-500 hover:text-white cursor-pointer"
          activeClassName="bg-blue-500 text-white"
          previousClassName="px-3 py-1 border rounded hover:bg-gray-200 cursor-pointer"
          nextClassName="px-3 py-1 border rounded hover:bg-gray-200 cursor-pointer"
          breakClassName="px-3 py-1"
        />
      </div>
    </div>
  );
}

export default MovieList;
